sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {

    // Add your code here

    try {
        var slots = current.task_slots;

        var listFieldValues = current.task_slots.toString().split(',');

        for (var i = 0; i < listFieldValues.length; i++) {
            var gr = new GlideRecord('x_1142001_now_mech_request_task');
            gr.initialize();
            gr.service_request = current.sys_id;
            var dailySlotGr = new GlideRecord('x_1142001_now_mech_slots_daily');
            dailySlotGr.addQuery('sys_id', listFieldValues[i]);
            dailySlotGr.query();
            var taskSysId;
            if (dailySlotGr.next()) {
                taskSysId = dailySlotGr.getValue('service_task');
            }
            gr.service_task = taskSysId;
            gr.slot = listFieldValues[i];
            gr.insert();
        }

    } catch (error) {
        gs.error("error in BR" + error);
    }

})(current, previous);
