sys_script_client.script:
function onLoad() {
    //Type appropriate comment here, and begin script below
    var ga = new GlideAjax('VehicleMethods');
    ga.addParam('sysparm_name', 'getVehicleTypes');
    ga.getXML(getVehicleTypes);
}

function getVehicleTypes(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    try {
		var options = JSON.parse(answer);
        g_form.clearOptions('vehilce_type');

        // Iterate through the options and add them to the choice field
        options.forEach(function(option) {
            g_form.addOption('vehilce_type', option.value, option.label);
        });

    } catch (error) {
        alert(error);
    }
}
