sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || isTemplate) {
        return;
    }

    //Type appropriate comment here, and begin script below

    var typeField = g_form.getValue('vehilce_type');
	var brandField = g_form.getValue('vehcile_brand');
    var ga = new GlideAjax('VehicleMethods');
    ga.addParam('sysparm_name', 'getVehicleModelsByBrand');
    ga.addParam('sysparm_vehicleType', typeField);
	ga.addParam('sysparm_vehicleBrand', brandField);
    ga.getXML(getVehicleModels);
}

function getVehicleModels(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var options = JSON.parse(answer);
	var model = g_form.getValue('vehicle_model');
    g_form.clearOptions('vehicle_model');

    // Iterate through the options and add them to the choice field
    options.forEach(function(option) {
        g_form.addOption('vehicle_model', option.value, option.label);
    });
	if(!g_form.isNewRecord()){
		g_form.setValue('vehicle_model', model);
	}
}
