function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || isTemplate) {
        return;
    }
    var dateFieldControl = g_form.getControl('requested_date');

    var currentDate = new Date();
    currentDate.setHours(0, 0, 0, 0);
    var selectedDate = new Date(dateFieldControl.value);
    selectedDate.setHours(0, 0, 0, 0);
    if (currentDate > selectedDate) {
        // Display an error message and clear the field
        g_form.addErrorMessage('Please select a future date.');
        dateFieldControl.value = ''; // Clear the date field
    } else {
        // var dateField = g_form.getValue('requested_date');
        // var serviceCentreField = g_form.getValue('service_centre');
        // var ga = new GlideAjax('serviceTask');
        // ga.addParam('sysparm_name', 'getTaskSlots');
        // ga.addParam('sysparm_requestDate', dateField);
        // ga.addParam('sysparm_serviceCentre', serviceCentreField);
        // ga.getXML(getSlots);
    }
}

