sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || isTemplate) {
        return;
    }

    //Type appropriate comment here, and begin script below

    var typeField = g_form.getValue('vehilce_type');
    var ga = new GlideAjax('VehicleMethods');
    ga.addParam('sysparm_name', 'getVehicleBrandsByType');
    ga.addParam('sysparm_vehicleType', typeField);
    ga.getXML(getVehicleBrandsByType);
}

function getVehicleBrandsByType(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var options = JSON.parse(answer);
    g_form.clearOptions('vehcile_brand');

    // Iterate through the options and add them to the choice field
    options.forEach(function(option) {
        g_form.addOption('vehcile_brand', option.value, option.label);
    });
}
