sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '' || isTemplate) {
        return;
    }

    //Type appropriate comment here, and begin script below

    var typeField = g_form.getValue('vehilce_type');
	var brandField = g_form.getValue('vehcile_brand');
	var modelField = g_form.getValue('vehicle_model');
    var ga = new GlideAjax('VehicleMethods');
    ga.addParam('sysparm_name', 'getVehicleFuelTypeByModel');
    ga.addParam('sysparm_vehicleType', typeField);
	ga.addParam('sysparm_vehicleBrand', brandField);
	ga.addParam('sysparm_vehicleModel', modelField);
    ga.getXML(getVehicleFuelTypes);
}

function getVehicleFuelTypes(response) {
    var answer = response.responseXML.documentElement.getAttribute("answer");
    var options = JSON.parse(answer);
	var fuelType = g_form.getValue('vehicle_fuel_type');
    g_form.clearOptions('vehicle_fuel_type');

    // Iterate through the options and add them to the choice field
    options.forEach(function(option) {
        g_form.addOption('vehicle_fuel_type', option.value, option.label);
    });
	if(!g_form.isNewRecord()){
		g_form.setValue('vehicle_fuel_type', fuelType);
	}
}
