sys_script_include.script:
var serviceTask = Class.create();
serviceTask.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	getTaskSlots:function(){
		try {
            var requestDate = this.getParameter('sysparm_requestDate');
			var serviceCentre = this.getParameter('sysparm_serviceCentre');
			var glideDate = new GlideDate();
			glideDate.setDisplayValue(requestDate);
			gs.info("date " + requestDate);
			gs.info("serviceCentre "+ serviceCentre);
            var dailySlotsGlideRecord = new GlideRecord('x_1142001_now_mech_slots_daily');
            dailySlotsGlideRecord.addQuery('date', glideDate);
			dailySlotsGlideRecord.addQuery('service_centre', serviceCentre);
			//dailySlotsGlideRecord.addQuery('is_available', true);
            dailySlotsGlideRecord.query();
            var result = [];
            while (dailySlotsGlideRecord.next()) {
				gs.info(dailySlotsGlideRecord.getValue('sys_id'));
                result.push(dailySlotsGlideRecord);
            }
            return JSON.stringify(result);

        } catch (error) {
            gs.error(error);
        }
	},
    type: 'serviceTask'
});
