sysevent_script_action.script:
try {
    var RequestTasksgliderecord = new GlideRecord('x_1142001_now_mech_request_task');

    RequestTasksgliderecord.addQuery('service_request', event.parm1);
    RequestTasksgliderecord.query();
    gs.info('parm1 ' + event.parm1);
    var totalPrice = 0;
    while (RequestTasksgliderecord.next()) {
        // do someting
        var Taskgr = new GlideRecord('x_1142001_now_mech_service_task');
        Taskgr.addQuery('sys_id', RequestTasksgliderecord.getValue('service_task'));
        // Taskgr.addQuery('state', 'IN', [3]);
        Taskgr.query();
        if (Taskgr.next()) {
            gs.info("price of " + RequestTasksgliderecord.getValue('service_task') + " is " + Taskgr.getValue('service_charge'));
            totalPrice += parseInt(Taskgr.getValue('service_charge'));
        }
        var Taskpartgr = new GlideRecord('x_1142001_now_mech_task_part');
        Taskpartgr.addQuery('service_task', RequestTasksgliderecord.getValue('sys_id'));
        Taskpartgr.query();
        while (Taskpartgr.next()) {
            gs.info("task part " + Taskpartgr.getValue('sys_id'));
            gs.info("spare_part sys_id " + Taskpartgr.getValue('spare_part'));

            var Partgr = new GlideRecord('x_1142001_now_mech_part');
            Partgr.addQuery('sys_id', Taskpartgr.getValue('spare_part'));
            Partgr.query();
            if (Partgr.next()) {
                gs.info("part loop sys_id " + Partgr.getValue('sys_id'));
                gs.info("part price " + Partgr.getValue('price'));

                totalPrice += parseInt(Partgr.getValue('price'));
            }
        }
    }

    gs.info("total price is " + totalPrice);

    var Invoicegr = new GlideRecord('x_1142001_now_mech_invoice');

    Invoicegr.initialize();
    Invoicegr.amount = totalPrice;
    Invoicegr.service_request = event.parm1;

    Invoicegr.insert();

    gs.info("inserted record in invoice table");

} catch (error) {
    gs.error("error in script" + error);
}
